</template>

<script>
    import Api from "@/services/api";

    export default {
        data() {
            return {
                hideOldPassword: true,
                hideNewPassword: true,
                hideConfirmPassword: true,
                processChangePassword: false,
                single: {
                    old_password: '',
                    new_password: '',
                    confirm_password: '',
                }
            }
        },
        methods: {
            isCurrentUrl(routeName){
            let response = false;
                for (let i = 0; i < routeName.length; i++) {
                    if (this.$route.name.indexOf(routeName[i]) != -1) {
                        return true;
                    }
                }

                return response;
            },
            showModalChangePassword() {
                this.hideOldPassword = true;
                this.hideNewPassword = true;
                this.hideConfirmPassword = true;

                this.single.old_password = '';
                this.single.new_password = '';
                this.single.confirm_password = '';

                this.processChangePassword = false;

                $("#modal-change-password").modal('show');
            },
            changePassword() {
                this.processChangePassword = true;

                let formData = {
                    currentPassword: this.single.old_password,
                    newPassword: this.single.new_password,
                    newPasswordConfirmation: this.single.confirm_password
                }

                Api().post(`${this.$apiType('global')}auth/change-password`, formData)
                    .then(response => {

                        $("#modal-change-password").modal('hide');

                        this.$swal({
                            title: "Berhasil!",
                            text: 'Mengubah Password Akun',
                            icon: "success",
                        });
                    })
                    .catch(error => {
                        this.$axiosHandleError(error);
                    }).then(() => {
                        this.processChangePassword = false;
                    });
            },
            logout() {
                Api().post(`${this.$apiType('global')}auth/logout`)
                    .then(response => {})
                    .catch(error => {}).then(() => {
                        localStorage.clear();
                        this.$store.commit('profile/SET_PROFILE_DATA', null);
                        this.$router.replace({
                            path: '/login'
                        })
                    });
            }
        },
            
        }

</script>


